import React from 'react';
import { Image, Text, View ,StyleSheet, Button} from 'react-native';
const ItemFlower = (props) => {
  const flower = props.data;

  const handleDetail = props.handleDetail;
 
    return (
        <View > 
            <View style={styles.div}>
                <Image 
                 style={styles.logo}
                  source={{
                    uri: flower.image_prd,
                  }}
                />
                <View style={styles.text_center}>
                    <Text>{flower.name_prd}</Text>
                    <Text style={styles.bold}>Gi√° : {flower.price_prd}</Text>
                    <Button onPress={()=>handleDetail(flower.id_prd)} title="xem chi tiet">

                    </Button>
                </View>
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    // itemFlower:{
    //     boxShadow:"1px 2px 3px 4px gray"
    // },
    bold:{
        fontWeight:"bold"
    },
    text_center:{
       textAlign: 'center'
    },
    div: {
      flexWrap: "wrap",
      width: "49%",
      backgroundColor: "#fff",
      display: "inline-table",
        // position: "relative",
    },
    logo: {
      width: "177px",
      height: "152px",
      borderRadius: "5px",
    },
    
    header: {
      backgroundColor: "#fff",
      textAlign: "center",
      fontWeight: "bold",
      fontSize: 50,
      color: "red",
      marginTop: 30,
    },
    list: {
      paddingHorizontal: 20,
      paddingTop: 40,
    },
    items: {
      backgroundColor: "pink",
      padding: 40,
      marginBottom: 10,
      fontSize: 50,
      borderRadius: 6,
    },
  });
  
export default ItemFlower;
