import React from 'react';
import { Image, Text, View ,StyleSheet} from 'react-native';
const ItemFlower = (props) => {
  const flower = props.data;
  const navigation = props.navigation;
  const handleDetail=(id)=>{
    navigation.navigate("Detail",{
      id:id
  })
  }
    return (
        <View>
            <View style={styles.itemFlower} onPress={handleDetail()}>
                <Image 
                 style={styles.logo}
        source={{
          uri: flower.image_prd,
        }}
                />
                <View style={styles.text_center}>
                    <Text>{flower.name_prd}</Text>
                    <Text style={styles.bold}>Gi√° : {flower.price_prd}</Text>
                </View>
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    itemFlower:{
        boxShadow:"1px 2px 3px 4px gray"
    },
    bold:{
        fontWeight:"bold"
    },
    text_center:{
       textAlign: 'center'
    },
    logo: {
        width: "177px",
        height: "152px",
    },
    container: {
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
    },
    header: {
      backgroundColor: "#fff",
      textAlign: "center",
      fontWeight: "bold",
      fontSize: 50,
      color: "red",
      marginTop: 30,
    },
    list: {
      paddingHorizontal: 20,
      paddingTop: 40,
    },
    items: {
      backgroundColor: "pink",
      padding: 40,
      marginBottom: 10,
      fontSize: 50,
      borderRadius: 6,
    },
  });
  
export default ItemFlower;
